<?php require_once __DIR__ . '/../core/db.php'; $db = db(); if($_SERVER['REQUEST_METHOD']==='POST'){ $mobile=$db->real_escape_string($_POST['mobile']); $pass=$_POST['password']; $r=$db->query("SELECT * FROM users WHERE mobile='$mobile'"); if(!$r||!$r->num_rows){ echo 'No user'; exit; } $u=$r->fetch_assoc(); if(password_verify($pass,$u['password_hash'])){ $_SESSION['user_id']=$u['id']; header('Location: dashboard.php'); exit; } else { echo 'Invalid'; } } ?><!doctype html><html><head><meta charset='utf-8'><meta name='viewport' content='width=device-width,initial-scale=1'><title>Login</title><link rel='stylesheet' href='../assets/css/style.css'></head><body><div class='container'><h2>Login</h2><form method='POST'><input name='mobile' placeholder='Mobile' required><br><input name='password' type='password' placeholder='Password' required><br><button class='btn' type='submit'>Login</button></form></div></body></html>