<?php require_once __DIR__ . '/../core/db.php'; $db = db(); if(empty($_SESSION['user_id'])) header('Location: login.php'); $uid=(int)$_SESSION['user_id']; $u=$db->query("SELECT wallet FROM users WHERE id=$uid")->fetch_assoc(); $tx=$db->query("SELECT * FROM wallet_transactions WHERE user_id=$uid ORDER BY created_at DESC"); ?><!doctype html><html><head><meta charset='utf-8'><meta name='viewport' content='width=device-width,initial-scale=1'><title>Wallet</title><link rel='stylesheet' href='../assets/css/style.css'></head><body><div class='container'><h2>Wallet</h2><div class='card'><p class='small'>Balance: ₹<?=number_format($u['wallet'],2)?></p><h4>Transactions</h4><?php while($t=$tx->fetch_assoc()): ?><div class='small'><?=htmlspecialchars($t['type'].' ₹'.number_format($t['amount'],2).' - '.($t['remark']?:''))?> <span style='float:right'><?=htmlspecialchars($t['created_at'])?></span></div><?php endwhile; ?></div><p><a class='small' href='dashboard.php'>&larr; Back</a></p></div></body></html>